val twt = tweets.window(Seconds(120))

case class Tweet(createdAt:Long, text:String, screenName:String)
twt.map(t=>
  Tweet(t.getCreatedAt().getTime()/1000, t.getText(), t.getUser().getScreenName())).foreachRDD(rdd=>{
    rdd.toDF().registerTempTable("tweets")
}
)

//twt.print

ssc.start()

/* declaring a function in Scala */

def sentiment(s:String) : String = {
    val positive = Array("like", "love", "good", "great", "happy", "cool", "the", "one", "that","golf","russia")
    val negative = Array("hate", "bad", "stupid", "hillary","collusion","cnn","fakenews","democrat","unknown","child","insecure","god","rocketman")

    var st = 0;

    val words = s.split(" ")    
    positive.foreach(p =>
        words.foreach(w =>
            if(p==w) st = st+1
        )
    )

    negative.foreach(p=>
        words.foreach(w=>
            if(p==w) st = st-1
        )
    )
    if(st>0)
        "positivie"
    else if(st<0)
        "negative"
    else
        "neutral"
}

sqlc.udf.register("sentiment", sentiment _)
